mapper.update:
update t_message
set memberid = #{memberid,jdbcType=INTEGER},
  content = #{content,jdbcType=VARCHAR},
  senddate = #{senddate,jdbcType=CHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, memberid, content, senddate
from t_message
where id = #{id,jdbcType=INTEGER}
