mapper.update:
update t_ticket set
 pstep = #{pstep}
 where memberid = #{memberid} and status = '0'

update t_ticket set
 pstep = #{pstep}, authcode = #{authcode}, piid = #{piid}
 where id = #{id}

mapper.select:
SELECT t_member.* FROM t_member JOIN t_ticket
 ON t_member.id = t_ticket.memberid
 WHERE t_ticket.piid = #{piid};
